// Address of Cross Contract Call precompile in Aurora.
// It allows scheduling new promises to NEAR contracts.
address constant XCC_PRECOMPILE = 0x516Cded1D16af10CAd47D6D49128E2eB7d27b372;
// Address of predecessor account id precompile in Aurora.
// It allows getting the predecessor account id of the current call.
address constant PREDECESSOR_ACCOUNT_ID_PRECOMPILE = 0x723FfBAbA940e75E7BF5F6d61dCbf8d9a4De0fD7;
// Address of current account id precompile in Aurora.
// It allows getting the current account id of the current call.
address constant CURRENT_ACCOUNT_ID_PRECOMPILE = 0xfeFAe79E4180Eb0284F261205E3F8CEA737afF56;
// Addresss of promise result precompile in Aurora.
address constant PROMISE_RESULT_PRECOMPILE = 0x0A3540F79BE10EF14890e87c1A0040A68Cc6AF71;
// Address of wNEAR ERC20 on mainnet
address constant wNEAR_MAINNET = 0xC42C30aC6Cc15faC9bD938618BcaA1a1FaE8501d;

struct NEAR {
    /// Wether the represenative NEAR account id for this contract
    /// has already been created or not. This is required since the
    /// first cross contract call requires attaching extra deposit
    /// to cover storage staking balance.
    bool initialized;
    /// Address of wNEAR token contract. It is used to charge the user
    /// required tokens for paying NEAR storage fees and attached balance
    /// for cross contract calls.
    IERC20 wNEAR;
}

library AuroraSdk {
    using Codec for bytes;
    using Codec for PromiseCreateArgs;
    using Codec for PromiseWithCallback;
    using Codec for Borsh.Data;
    using Borsh for Borsh.Data;

    /// Create an instance of NEAR object. Requires the address at which
    /// wNEAR ERC20 token contract is deployed.
    function initNear(IERC20 wNEAR) public returns (NEAR memory) {
        // Innovation silo doens't have wNEAR deployed, call fund_xcc_sub_account.
        NEAR memory near = NEAR(true, wNEAR);
        near.wNEAR.approve(XCC_PRECOMPILE, 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff);
        return near;
    }

    /// Default configuration for mainnet.
    function mainnet() public returns (NEAR memory) {
        return initNear(IERC20(wNEAR_MAINNET));
    }

    /// Compute NEAR represtentative account for the given Aurora address.
    /// This is the NEAR account created by the cross contract call precompile.
    function nearRepresentative(address account) public returns (string memory) {
        return addressSubAccount(account, currentAccountId());
    }

    /// Prepends the given account ID with the given address (hex-encoded).
    function addressSubAccount(address account, string memory accountId) public pure returns (string memory) {
        return string(abi.encodePacked(Utils.bytesToHex(abi.encodePacked((bytes20(account)))), ".", accountId));
    }

    /// Compute implicity Aurora Address for the given NEAR account.
    function implicitAuroraAddress(string memory accountId) public pure returns (address) {
        return address(uint160(uint256(keccak256(bytes(accountId)))));
    }

    /// Compute the implicit Aurora address of the represenative NEAR account
    /// for the given Aurora address. Useful when a contract wants to call
    /// itself via a callback using cross contract call precompile.
    function nearRepresentitiveImplicitAddress(address account) public returns (address) {
        return implicitAuroraAddress(nearRepresentative(account));
    }

    /// Get the promise result at the specified index.
    function promiseResult(uint256 index) public returns (PromiseResult memory result) {
        (bool success, bytes memory returnData) = PROMISE_RESULT_PRECOMPILE.call("");
        require(success);

        Borsh.Data memory borsh = Borsh.from(returnData);

        uint32 length = borsh.decodeU32();
        require(index < length, "Index out of bounds");

        for (uint256 i = 0; i < index; i++) {
            PromiseResultStatus status = PromiseResultStatus(uint8(borsh.decodeU8()));
            if (status == PromiseResultStatus.Successful) {
                borsh.skipBytes();
            }
        }

        result.status = PromiseResultStatus(borsh.decodeU8());
        if (result.status == PromiseResultStatus.Successful) {
            result.output = borsh.decodeBytes();
        }
    }

    /// Get the NEAR account id of the current contract. It is the account id of Aurora engine.
    function currentAccountId() public returns (string memory) {
        (bool success, bytes memory returnData) = CURRENT_ACCOUNT_ID_PRECOMPILE.call("");
        require(success);
        return string(returnData);
    }

    /// Get the NEAR account id of the predecessor contract.
    function predecessorAccountId() public returns (string memory) {
        (bool success, bytes memory returnData) = PREDECESSOR_ACCOUNT_ID_PRECOMPILE.call("");
        require(success);
        return string(returnData);
    }

    /// Creates a base promise. This is not immediately scheduled for execution
    /// until transact is called. It can be combined with other promises using
    /// `then` combinator.
    ///
    /// Input is not checekd during promise creation. If it is invalid, the
    /// transaction will be scheduled either way, but it will fail during execution.
    function call(
        NEAR storage near,
        string memory targetAccountId,
        string memory method,
        bytes memory args,
        uint128 nearBalance,
        uint64 nearGas
    ) public returns (PromiseCreateArgs memory) {
        /// Need to capture nearBalance before we modify it so that we don't
        /// double-charge the user for their initialization cost.
        PromiseCreateArgs memory promise_args = PromiseCreateArgs(targetAccountId, method, args, nearBalance, nearGas);

        if (!near.initialized) {
            /// If the contract needs to be initialized, we need to attach
            /// 2 NEAR (= 2 * 10^24 yoctoNEAR) to the promise.
            nearBalance += 2_000_000_000_000_000_000_000_000;
            near.initialized = true;
        }

        if (nearBalance > 0) {
            near.wNEAR.transferFrom(msg.sender, address(this), uint256(nearBalance));
        }

        return promise_args;
    }

    /// Similar to `call`. It is a wrapper that simplifies the creation of a promise
    /// to a contract inside `Aurora`.
    function auroraCall(NEAR storage near, address target, bytes memory args, uint128 nearBalance, uint64 nearGas)
        public
        returns (PromiseCreateArgs memory)
    {
        return call(
            near,
            currentAccountId(),
            "call",
            abi.encodePacked(uint8(0), target, uint256(0), args.encode()),
            nearBalance,
            nearGas
        );
    }

    /// Schedule a base promise to be executed on NEAR. After this function is called
    /// the promise should not be used anymore (i.e. should not be used with any further combinators).
    /// The promise also cannot be used to obtain any result of the call. A result can only be used
    /// by attaching a callback using `then` prior to calling `transact`.
    function transact(PromiseCreateArgs memory nearPromise) public {
        (bool success, bytes memory returnData) =
            XCC_PRECOMPILE.call(nearPromise.encodeCrossContractCallArgs(ExecutionMode.Eager));

        if (!success) {
            revert(string(returnData));
        }
    }

    /// Schedule a promise with callback to be executed on NEAR. After this function is called
    /// the promise should not be used anymore.
    ///
    /// Duplicated due to lack of generics in solidity. Check relevant issue:
    /// https://github.com/ethereum/solidity/issues/869
    function transact(PromiseWithCallback memory nearPromise) public {
        (bool success, bytes memory returnData) =
            XCC_PRECOMPILE.call(nearPromise.encodeCrossContractCallArgs(ExecutionMode.Eager));

        if (!success) {
            revert(string(returnData));
        }
    }

    /// Similar to `transact`, except the promise is not executed as part of the same transaction.
    /// A separate transaction to execute the scheduled promise is needed.
    function lazy_transact(PromiseCreateArgs memory nearPromise) public {
        (bool success, bytes memory returnData) =
            XCC_PRECOMPILE.call(nearPromise.encodeCrossContractCallArgs(ExecutionMode.Lazy));

        if (!success) {
            revert(string(returnData));
        }
    }

    function lazy_transact(PromiseWithCallback memory nearPromise) public {
        (bool success, bytes memory returnData) =
            XCC_PRECOMPILE.call(nearPromise.encodeCrossContractCallArgs(ExecutionMode.Lazy));

        if (!success) {
            revert(string(returnData));
        }
    }

    /// Create a promise with callback from two given promises.
    function then(PromiseCreateArgs memory base, PromiseCreateArgs memory callback)
        public
        pure
        returns (PromiseWithCallback memory)
    {
        return PromiseWithCallback(base, callback);
    }
}

contracts/aurora-sdk/Borsh.sol.content:
// SPDX-License-Identifier: CC-BY-1.0
pragma solidity ^0.8.20;

import "./Utils.sol";

library Borsh {
    using Borsh for Data;

    struct Data {
        uint256 ptr;
        uint256 end;
    }

    function from(bytes memory data) internal pure returns (Data memory res) {
        uint256 ptr;
        assembly {
            ptr := data
        }
        unchecked {
            res.ptr = ptr + 32;
            res.end = res.ptr + Utils.readMemory(ptr);
        }
    }

    // This function assumes that length is reasonably small, so that data.ptr + length will not overflow. In the current code, length is always less than 2^32.
    function requireSpace(Data memory data, uint256 length) internal pure {
        unchecked {
            require(data.ptr + length <= data.end, "Parse error: unexpected EOI");
        }
    }

    function read(Data memory data, uint256 length) internal pure returns (bytes32 res) {
        data.requireSpace(length);
        res = bytes32(Utils.readMemory(data.ptr));
        unchecked {
            data.ptr += length;
        }
        return res;
    }

    function done(Data memory data) internal pure {
        require(data.ptr == data.end, "Parse error: EOI expected");
    }

    // Same considerations as for requireSpace.
    function peekKeccak256(Data memory data, uint256 length) internal pure returns (bytes32) {
        data.requireSpace(length);
        return Utils.keccak256Raw(data.ptr, length);
    }

    // Same considerations as for requireSpace.
    function peekSha256(Data memory data, uint256 length) internal view returns (bytes32) {
        data.requireSpace(length);
        return Utils.sha256Raw(data.ptr, length);
    }

    function decodeU8(Data memory data) internal pure returns (uint8) {
        return uint8(bytes1(data.read(1)));
    }

    function decodeU16(Data memory data) internal pure returns (uint16) {
        return Utils.swapBytes2(uint16(bytes2(data.read(2))));
    }

    function decodeU32(Data memory data) internal pure returns (uint32) {
        return Utils.swapBytes4(uint32(bytes4(data.read(4))));
    }

    function decodeU64(Data memory data) internal pure returns (uint64) {
        return Utils.swapBytes8(uint64(bytes8(data.read(8))));
    }

    function decodeU128(Data memory data) internal pure returns (uint128) {
        return Utils.swapBytes16(uint128(bytes16(data.read(16))));
    }

    function decodeU256(Data memory data) internal pure returns (uint256) {
        return Utils.swapBytes32(uint256(data.read(32)));
    }

    function decodeBytes20(Data memory data) internal pure returns (bytes20) {
        return bytes20(data.read(20));
    }

    function decodeBytes32(Data memory data) internal pure returns (bytes32) {
        return data.read(32);
    }

    function decodeBool(Data memory data) internal pure returns (bool) {
        uint8 res = data.decodeU8();
        require(res <= 1, "Parse error: invalid bool");
        return res != 0;
    }

    function skipBytes(Data memory data) internal pure {
        uint256 length = data.decodeU32();
        data.requireSpace(length);
        unchecked {
            data.ptr += length;
        }
    }

    function decodeBytes(Data memory data) internal pure returns (bytes memory res) {
        uint256 length = data.decodeU32();
        data.requireSpace(length);
        res = Utils.memoryToBytes(data.ptr, length);
        unchecked {
            data.ptr += length;
        }
    }
}

contracts/aurora-sdk/Codec.sol.content:
// SPDX-License-Identifier: CC-BY-1.0
pragma solidity ^0.8.20;

import "./Borsh.sol";
import "./Types.sol";
import "./Utils.sol";

/// Provide borsh serialization and deserialization for multiple types.
library Codec {
    using Borsh for Borsh.Data;

    function encodeU8(uint8 v) internal pure returns (bytes1) {
        return bytes1(v);
    }

    function encodeU16(uint16 v) internal pure returns (bytes2) {
        return bytes2(Utils.swapBytes2(v));
    }

    function encodeU32(uint32 v) public pure returns (bytes4) {
        return bytes4(Utils.swapBytes4(v));
    }

    function encodeU64(uint64 v) public pure returns (bytes8) {
        return bytes8(Utils.swapBytes8(v));
    }

    function encodeU128(uint128 v) public pure returns (bytes16) {
        return bytes16(Utils.swapBytes16(v));
    }

    /// Encode bytes into borsh. Use this method to encode strings as well.
    function encode(bytes memory value) public pure returns (bytes memory) {
        return abi.encodePacked(encodeU32(uint32(value.length)), bytes(value));
    }

    /// Encode Execution mode enum into borsh.
    function encode(ExecutionMode mode) public pure returns (bytes1) {
        return bytes1(uint8(mode));
    }

    /// Encode PromiseArgsVariant enum into borsh.
    function encode(PromiseArgsVariant mode) public pure returns (bytes1) {
        return bytes1(uint8(mode));
    }

    /// Encode base promise into borsh.
    function encode(PromiseCreateArgs memory nearPromise) public pure returns (bytes memory) {
        return abi.encodePacked(
            encode(bytes(nearPromise.targetAccountId)),
            encode(bytes(nearPromise.method)),
            encode(nearPromise.args),
            encodeU128(nearPromise.nearBalance),
            encodeU64(nearPromise.nearGas)
        );
    }

    /// Encode promise with callback into borsh.
    function encode(PromiseWithCallback memory nearPromise) public pure returns (bytes memory) {
        return abi.encodePacked(encode(nearPromise.base), encode(nearPromise.callback));
    }

    /// Encode create promise using borsh. The encoded data
    /// uses the same format that the Cross Contract Call precompile expects.
    function encodeCrossContractCallArgs(PromiseCreateArgs memory nearPromise, ExecutionMode mode)
        public
        pure
        returns (bytes memory)
    {
        return abi.encodePacked(encode(mode), encode(PromiseArgsVariant.Create), encode(nearPromise));
    }

    /// Encode promise with callback using borsh. The encoded data
    /// uses the same format that the Cross Contract Call precompile expects.
    function encodeCrossContractCallArgs(PromiseWithCallback memory nearPromise, ExecutionMode mode)
        public
        pure
        returns (bytes memory)
    {
        return abi.encodePacked(encode(mode), encode(PromiseArgsVariant.Callback), encode(nearPromise));
    }

    /// Decode promise result using borsh.
    function decodePromiseResult(Borsh.Data memory data) public pure returns (PromiseResult memory result) {
        result.status = PromiseResultStatus(data.decodeU8());
        if (result.status == PromiseResultStatus.Successful) {
            result.output = data.decodeBytes();
        }
    }

    /// Skip promise result from the buffer.
    function skipPromiseResult(Borsh.Data memory data) public pure {
        PromiseResultStatus status = PromiseResultStatus(uint8(data.decodeU8()));
        if (status == PromiseResultStatus.Successful) {
            data.skipBytes();
        }
    }
}

contracts/aurora-sdk/Types.sol.content:
// SPDX-License-Identifier: CC-BY-1.0
pragma solidity ^0.8.20;

/// Basic NEAR promise.
struct PromiseCreateArgs {
    /// Account id of the target contract to be called.
    string targetAccountId;
    /// Method in the contract to be called
    string method;
    /// Payload to be passed to the method as input.
    bytes args;
    /// Amount of NEAR tokens to attach to the call. This will
    /// be charged from the caller in wNEAR.
    uint128 nearBalance;
    /// Amount of gas to attach to the call.
    uint64 nearGas;
}

enum PromiseArgsVariant
/// Basic NEAR promise
{
    Create,
    /// NEAR promise with a callback attached.
    Callback,
    /// Description of arbitrary NEAR promise. Allows applying combinators
    /// recursively, multiple action types and batched actions.
    /// See https://nomicon.io/RuntimeSpec/Components/BindingsSpec/PromisesAPI
    /// for a complete description of what is possible.
    Recursive
}

/// Combine two base promises using NEAR combinator `then`.
struct PromiseWithCallback {
    /// Initial promise to be triggered.
    PromiseCreateArgs base;
    /// Second promise that is executed after the execution of `base`.
    /// In particular this promise will have access to the result of
    /// the `base` promise.
    PromiseCreateArgs callback;
}

enum ExecutionMode
/// Eager mode means that the promise WILL be executed in a single
/// NEAR transaction.
{
    Eager,
    /// Lazy mode means that the promise WILL be scheduled for execution
    /// and a separate interaction is required to trigger this execution.
    Lazy
}

enum PromiseResultStatus
/// This status should not be reachable.
{
    NotReady,
    /// The promise was executed successfully.
    Successful,
    /// The promise execution failed.
    Failed
}

struct PromiseResult {
    /// Status result of the promise execution.
    PromiseResultStatus status;
    /// If the status is successful, output contains the output of the promise.
    /// Otherwise the output field MUST be ignored.
    bytes output;
}

contracts/aurora-sdk/Utils.sol.content:
// SPDX-License-Identifier: CC-BY-1.0
pragma solidity ^0.8.20;

library Utils {
    function swapBytes2(uint16 v) internal pure returns (uint16) {
        return (v << 8) | (v >> 8);
    }

    function swapBytes4(uint32 v) internal pure returns (uint32) {
        v = ((v & 0x00ff00ff) << 8) | ((v & 0xff00ff00) >> 8);
        return (v << 16) | (v >> 16);
    }

    function swapBytes8(uint64 v) internal pure returns (uint64) {
        v = ((v & 0x00ff00ff00ff00ff) << 8) | ((v & 0xff00ff00ff00ff00) >> 8);
        v = ((v & 0x0000ffff0000ffff) << 16) | ((v & 0xffff0000ffff0000) >> 16);
        return (v << 32) | (v >> 32);
    }

    function swapBytes16(uint128 v) internal pure returns (uint128) {
        v = ((v & 0x00ff00ff00ff00ff00ff00ff00ff00ff) << 8) | ((v & 0xff00ff00ff00ff00ff00ff00ff00ff00) >> 8);
        v = ((v & 0x0000ffff0000ffff0000ffff0000ffff) << 16) | ((v & 0xffff0000ffff0000ffff0000ffff0000) >> 16);
        v = ((v & 0x00000000ffffffff00000000ffffffff) << 32) | ((v & 0xffffffff00000000ffffffff00000000) >> 32);
        return (v << 64) | (v >> 64);
    }

    function swapBytes32(uint256 v) internal pure returns (uint256) {
        v = ((v & 0x00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff) << 8)
            | ((v & 0xff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00ff00) >> 8);
        v = ((v & 0x0000ffff0000ffff0000ffff0000ffff0000ffff0000ffff0000ffff0000ffff) << 16)
            | ((v & 0xffff0000ffff0000ffff0000ffff0000ffff0000ffff0000ffff0000ffff0000) >> 16);
        v = ((v & 0x00000000ffffffff00000000ffffffff00000000ffffffff00000000ffffffff) << 32)
            | ((v & 0xffffffff00000000ffffffff00000000ffffffff00000000ffffffff00000000) >> 32);
        v = ((v & 0x0000000000000000ffffffffffffffff0000000000000000ffffffffffffffff) << 64)
            | ((v & 0xffffffffffffffff0000000000000000ffffffffffffffff0000000000000000) >> 64);
        return (v << 128) | (v >> 128);
    }

    function readMemory(uint256 ptr) internal pure returns (uint256 res) {
        assembly {
            res := mload(ptr)
        }
    }

    function writeMemory(uint256 ptr, uint256 value) internal pure {
        assembly {
            mstore(ptr, value)
        }
    }

    function memoryToBytes(uint256 ptr, uint256 length) internal pure returns (bytes memory res) {
        if (length != 0) {
            assembly {
                // 0x40 is the address of free memory pointer.
                res := mload(0x40)
                let end :=
                    add(res, and(add(length, 63), 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffe0))
                // end = res + 32 + 32 * ceil(length / 32).
                mstore(0x40, end)
                mstore(res, length)
                let destPtr := add(res, 32)
                // prettier-ignore
                for {} 1 {} {
                    mstore(destPtr, mload(ptr))
                    destPtr := add(destPtr, 32)
                    if eq(destPtr, end) { break }
                    ptr := add(ptr, 32)
                }
            }
        }
    }

    function keccak256Raw(uint256 ptr, uint256 length) internal pure returns (bytes32 res) {
        assembly {
            res := keccak256(ptr, length)
        }
    }

    function sha256Raw(uint256 ptr, uint256 length) internal view returns (bytes32 res) {
        assembly {
            // 2 is the address of SHA256 precompiled contract.
            // First 64 bytes of memory can be used as scratch space.
            let ret := staticcall(gas(), 2, ptr, length, 0, 32)
            // If the call to SHA256 precompile ran out of gas, burn any gas that remains.
            // prettier-ignore
            for {} iszero(ret) {} {}
            res := mload(0)
        }
    }

    /// Convert array of bytes to hexadecimal string.
    /// https://ethereum.stackexchange.com/a/126928/45323
    function bytesToHex(bytes memory buffer) public pure returns (string memory) {
        // Fixed buffer size for hexadecimal convertion
        bytes memory converted = new bytes(buffer.length * 2);

        bytes memory _base = "0123456789abcdef";

        for (uint256 i = 0; i < buffer.length; i++) {
            converted[i * 2] = _base[uint8(buffer[i]) / _base.length];
            converted[i * 2 + 1] = _base[uint8(buffer[i]) % _base.length];
        }

        return string(converted);
    }
}

contracts/BytesLib.sol.content:
// SPDX-License-Identifier: Unlicense
/*
 * @title Solidity Bytes Arrays Utils
 * @author Gonalo S <goncalo.sa@consensys.net>
 *
 * @dev Bytes tightly packed arrays utility library for ethereum contracts written in Solidity.
 *      The library lets you concatenate, slice and type cast bytes arrays both in memory and storage.
 */
pragma solidity >=0.8.0 <0.9.0;


library BytesLib {
    function concat(
        bytes memory _preBytes,
        bytes memory _postBytes
    )
        internal
        pure
        returns (bytes memory)
    {
        bytes memory tempBytes;

        assembly {
            // Get a location of some free memory and store it in tempBytes as
            // Solidity does for memory variables.
            tempBytes := mload(0x40)

            // Store the length of the first bytes array at the beginning of
            // the memory for tempBytes.
            let length := mload(_preBytes)
            mstore(tempBytes, length)

            // Maintain a memory counter for the current write location in the
            // temp bytes array by adding the 32 bytes for the array length to
            // the starting location.
            let mc := add(tempBytes, 0x20)
            // Stop copying when the memory counter reaches the length of the
            // first bytes array.
            let end := add(mc, length)

            for {
                // Initialize a copy counter to the start of the _preBytes data,
                // 32 bytes into its memory.
                let cc := add(_preBytes, 0x20)
            } lt(mc, end) {
                // Increase both counters by 32 bytes each iteration.
                mc := add(mc, 0x20)
                cc := add(cc, 0x20)
            } {
                // Write the _preBytes data into the tempBytes memory 32 bytes
                // at a time.
                mstore(mc, mload(cc))
            }

            // Add the length of _postBytes to the current length of tempBytes
            // and store it as the new length in the first 32 bytes of the
            // tempBytes memory.
            length := mload(_postBytes)
            mstore(tempBytes, add(length, mload(tempBytes)))

            // Move the memory counter back from a multiple of 0x20 to the
            // actual end of the _preBytes data.
            mc := end
            // Stop copying when the memory counter reaches the new combined
            // length of the arrays.
            end := add(mc, length)

            for {
                let cc := add(_postBytes, 0x20)
            } lt(mc, end) {
                mc := add(mc, 0x20)
                cc := add(cc, 0x20)
            } {
                mstore(mc, mload(cc))
            }

            // Update the free-memory pointer by padding our last write location
            // to 32 bytes: add 31 bytes to the end of tempBytes to move to the
            // next 32 byte block, then round down to the nearest multiple of
            // 32. If the sum of the length of the two arrays is zero then add
            // one before rounding down to leave a blank 32 bytes (the length block with 0).
            mstore(0x40, and(
              add(add(end, iszero(add(length, mload(_preBytes)))), 31),
              not(31) // Round down to the nearest 32 bytes.
            ))
        }

        return tempBytes;
    }

    function concatStorage(bytes storage _preBytes, bytes memory _postBytes) internal {
        assembly {
            // Read the first 32 bytes of _preBytes storage, which is the length
            // of the array. (We don't need to use the offset into the slot
            // because arrays use the entire slot.)
            let fslot := sload(_preBytes.slot)
            // Arrays of 31 bytes or less have an even value in their slot,
            // while longer arrays have an odd value. The actual length is
            // the slot divided by two for odd values, and the lowest order
            // byte divided by two for even values.
            // If the slot is even, bitwise and the slot with 255 and divide by
            // two to get the length. If the slot is odd, bitwise and the slot
            // with -1 and divide by two.
            let slength := div(and(fslot, sub(mul(0x100, iszero(and(fslot, 1))), 1)), 2)
            let mlength := mload(_postBytes)
            let newlength := add(slength, mlength)
            // slength can contain both the length and contents of the array
            // if length < 32 bytes so let's prepare for that
            // v. http://solidity.readthedocs.io/en/latest/miscellaneous.html#layout-of-state-variables-in-storage
            switch add(lt(slength, 32), lt(newlength, 32))
            case 2 {
                // Since the new array still fits in the slot, we just need to
                // update the contents of the slot.
                // uint256(bytes_storage) = uint256(bytes_storage) + uint256(bytes_memory) + new_length
                sstore(
                    _preBytes.slot,
                    // all the modifications to the slot are inside this
                    // next block
                    add(
                        // we can just add to the slot contents because the
                        // bytes we want to change are the LSBs
                        fslot,
                        add(
                            mul(
                                div(
                                    // load the bytes from memory
                                    mload(add(_postBytes, 0x20)),
                                    // zero all bytes to the right
                                    exp(0x100, sub(32, mlength))
                                ),
                                // and now shift left the number of bytes to
                                // leave space for the length in the slot
                                exp(0x100, sub(32, newlength))
                            ),
                            // increase length by the double of the memory
                            // bytes length
                            mul(mlength, 2)
                        )
                    )
                )
            }
            case 1 {
                // The stored value fits in the slot, but the combined value
                // will exceed it.
                // get the keccak hash to get the contents of the array
                mstore(0x0, _preBytes.slot)
                let sc := add(keccak256(0x0, 0x20), div(slength, 32))

                // save new length
                sstore(_preBytes.slot, add(mul(newlength, 2), 1))

                // The contents of the _postBytes array start 32 bytes into
                // the structure. Our first read should obtain the `submod`
                // bytes that can fit into the unused space in the last word
                // of the stored array. To get this, we read 32 bytes starting
                // from `submod`, so the data we read overlaps with the array
                // contents by `submod` bytes. Masking the lowest-order
                // `submod` bytes allows us to add that value directly to the
                // stored value.

                let submod := sub(32, slength)
                let mc := add(_postBytes, submod)
                let end := add(_postBytes, mlength)
                let mask := sub(exp(0x100, submod), 1)

                sstore(
                    sc,
                    add(
                        and(
                            fslot,
                            0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff00
                        ),
                        and(mload(mc), mask)
                    )
                )

                for {
                    mc := add(mc, 0x20)
                    sc := add(sc, 1)
                } lt(mc, end) {
                    sc := add(sc, 1)
                    mc := add(mc, 0x20)
                } {
                    sstore(sc, mload(mc))
                }

                mask := exp(0x100, sub(mc, end))

                sstore(sc, mul(div(mload(mc), mask), mask))
            }
            default {
                // get the keccak hash to get the contents of the array
                mstore(0x0, _preBytes.slot)
                // Start copying to the last used word of the stored array.
                let sc := add(keccak256(0x0, 0x20), div(slength, 32))

                // save new length
                sstore(_preBytes.slot, add(mul(newlength, 2), 1))

                // Copy over the first `submod` bytes of the new data as in
                // case 1 above.
                let slengthmod := mod(slength, 32)
                let mlengthmod := mod(mlength, 32)
                let submod := sub(32, slengthmod)
                let mc := add(_postBytes, submod)
                let end := add(_postBytes, mlength)
                let mask := sub(exp(0x100, submod), 1)

                sstore(sc, add(sload(sc), and(mload(mc), mask)))

                for {
                    sc := add(sc, 1)
                    mc := add(mc, 0x20)
                } lt(mc, end) {
                    sc := add(sc, 1)
                    mc := add(mc, 0x20)
                } {
                    sstore(sc, mload(mc))
                }

                mask := exp(0x100, sub(mc, end))

                sstore(sc, mul(div(mload(mc), mask), mask))
            }
        }
    }

    function slice(
        bytes memory _bytes,
        uint256 _start,
        uint256 _length
    )
        internal
        pure
        returns (bytes memory)
    {
        require(_length + 31 >= _length, "slice_overflow");
        require(_bytes.length >= _start + _length, "slice_outOfBounds");

        bytes memory tempBytes;

        assembly {
            switch iszero(_length)
            case 0 {
                // Get a location of some free memory and store it in tempBytes as
                // Solidity does for memory variables.
                tempBytes := mload(0x40)

                // The first word of the slice result is potentially a partial
                // word read from the original array. To read it, we calculate
                // the length of that partial word and start copying that many
                // bytes into the array. The first word we copy will start with
                // data we don't care about, but the last `lengthmod` bytes will
                // land at the beginning of the contents of the new array. When
                // we're done copying, we overwrite the full first word with
                // the actual length of the slice.
                let lengthmod := and(_length, 31)

                // The multiplication in the next line is necessary
                // because when slicing multiples of 32 bytes (lengthmod == 0)
                // the following copy loop was copying the origin's length
                // and then ending prematurely not copying everything it should.
                let mc := add(add(tempBytes, lengthmod), mul(0x20, iszero(lengthmod)))
                let end := add(mc, _length)

                for {
                    // The multiplication in the next line has the same exact purpose
                    // as the one above.
                    let cc := add(add(add(_bytes, lengthmod), mul(0x20, iszero(lengthmod))), _start)
                } lt(mc, end) {
                    mc := add(mc, 0x20)
                    cc := add(cc, 0x20)
                } {
                    mstore(mc, mload(cc))
                }

                mstore(tempBytes, _length)

                //update free-memory pointer
                //allocating the array padded to 32 bytes like the compiler does now
                mstore(0x40, and(add(mc, 31), not(31)))
            }
            //if we want a zero-length slice let's just return a zero-length array
            default {
                tempBytes := mload(0x40)
                //zero out the 32 bytes slice we are about to return
                //we need to do it because Solidity does not garbage collect
                mstore(tempBytes, 0)

                mstore(0x40, add(tempBytes, 0x20))
            }
        }

        return tempBytes;
    }

    function toAddress(bytes memory _bytes, uint256 _start) internal pure returns (address) {
        require(_bytes.length >= _start + 20, "toAddress_outOfBounds");
        address tempAddress;

        assembly {
            tempAddress := div(mload(add(add(_bytes, 0x20), _start)), 0x1000000000000000000000000)
        }

        return tempAddress;
    }

    function toUint8(bytes memory _bytes, uint256 _start) internal pure returns (uint8) {
        require(_bytes.length >= _start + 1 , "toUint8_outOfBounds");
        uint8 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x1), _start))
        }

        return tempUint;
    }

    function toUint16(bytes memory _bytes, uint256 _start) internal pure returns (uint16) {
        require(_bytes.length >= _start + 2, "toUint16_outOfBounds");
        uint16 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x2), _start))
        }

        return tempUint;
    }

    function toUint32(bytes memory _bytes, uint256 _start) internal pure returns (uint32) {
        require(_bytes.length >= _start + 4, "toUint32_outOfBounds");
        uint32 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x4), _start))
        }

        return tempUint;
    }

    function toUint64(bytes memory _bytes, uint256 _start) internal pure returns (uint64) {
        require(_bytes.length >= _start + 8, "toUint64_outOfBounds");
        uint64 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x8), _start))
        }

        return tempUint;
    }

    function toUint96(bytes memory _bytes, uint256 _start) internal pure returns (uint96) {
        require(_bytes.length >= _start + 12, "toUint96_outOfBounds");
        uint96 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0xc), _start))
        }

        return tempUint;
    }

    function toUint128(bytes memory _bytes, uint256 _start) internal pure returns (uint128) {
        require(_bytes.length >= _start + 16, "toUint128_outOfBounds");
        uint128 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x10), _start))
        }

        return tempUint;
    }

    function toUint256(bytes memory _bytes, uint256 _start) internal pure returns (uint256) {
        require(_bytes.length >= _start + 32, "toUint256_outOfBounds");
        uint256 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x20), _start))
        }

        return tempUint;
    }

    function toBytes32(bytes memory _bytes, uint256 _start) internal pure returns (bytes32) {
        require(_bytes.length >= _start + 32, "toBytes32_outOfBounds");
        bytes32 tempBytes32;

        assembly {
            tempBytes32 := mload(add(add(_bytes, 0x20), _start))
        }

        return tempBytes32;
    }

    function equal(bytes memory _preBytes, bytes memory _postBytes) internal pure returns (bool) {
        bool success = true;

        assembly {
            let length := mload(_preBytes)

            // if lengths don't match the arrays are not equal
            switch eq(length, mload(_postBytes))
            case 1 {
                // cb is a circuit breaker in the for loop since there's
                //  no said feature for inline assembly loops
                // cb = 1 - don't breaker
                // cb = 0 - break
                let cb := 1

                let mc := add(_preBytes, 0x20)
                let end := add(mc, length)

                for {
                    let cc := add(_postBytes, 0x20)
                // the next line is the loop condition:
                // while(uint256(mc < end) + cb == 2)
                } eq(add(lt(mc, end), cb), 2) {
                    mc := add(mc, 0x20)
                    cc := add(cc, 0x20)
                } {
                    // if any of these checks fails then arrays are not equal
                    if iszero(eq(mload(mc), mload(cc))) {
                        // unsuccess:
                        success := 0
                        cb := 0
                    }
                }
            }
            default {
                // unsuccess:
                success := 0
            }
        }

        return success;
    }

    function equalStorage(
        bytes storage _preBytes,
        bytes memory _postBytes
    )
        internal
        view
        returns (bool)
    {
        bool success = true;

        assembly {
            // we know _preBytes_offset is 0
            let fslot := sload(_preBytes.slot)
            // Decode the length of the stored array like in concatStorage().
            let slength := div(and(fslot, sub(mul(0x100, iszero(and(fslot, 1))), 1)), 2)
            let mlength := mload(_postBytes)

            // if lengths don't match the arrays are not equal
            switch eq(slength, mlength)
            case 1 {
                // slength can contain both the length and contents of the array
                // if length < 32 bytes so let's prepare for that
                // v. http://solidity.readthedocs.io/en/latest/miscellaneous.html#layout-of-state-variables-in-storage
                if iszero(iszero(slength)) {
                    switch lt(slength, 32)
                    case 1 {
                        // blank the last byte which is the length
                        fslot := mul(div(fslot, 0x100), 0x100)

                        if iszero(eq(fslot, mload(add(_postBytes, 0x20)))) {
                            // unsuccess:
                            success := 0
                        }
                    }
                    default {
                        // cb is a circuit breaker in the for loop since there's
                        //  no said feature for inline assembly loops
                        // cb = 1 - don't breaker
                        // cb = 0 - break
                        let cb := 1

                        // get the keccak hash to get the contents of the array
                        mstore(0x0, _preBytes.slot)
                        let sc := keccak256(0x0, 0x20)

                        let mc := add(_postBytes, 0x20)
                        let end := add(mc, mlength)

                        // the next line is the loop condition:
                        // while(uint256(mc < end) + cb == 2)
                        for {} eq(add(lt(mc, end), cb), 2) {
                            sc := add(sc, 1)
                            mc := add(mc, 0x20)
                        } {
                            if iszero(eq(sload(sc), mload(mc))) {
                                // unsuccess:
                                success := 0
                                cb := 0
                            }
                        }
                    }
                }
            }
            default {
                // unsuccess:
                success := 0
            }
        }

        return success;
    }
}

contracts/PythOracle.sol.content:
// SPDX-License-Identifier: CC-BY-1.0
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {AuroraSdk, Codec, NEAR, PromiseCreateArgs, PromiseResultStatus, PromiseWithCallback, Borsh} from "./aurora-sdk/AuroraSdk.sol";
import {BytesLib} from "./BytesLib.sol";
import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";

// When making a call to another NEAR contract, you must specify how much NEAR gas
// will be attached to the call (this is simlar to the `gas` argument in the EVM `call` opcode).
// The typical unit of has on Near is the teragas (Tgas), where 1 Tgas = 10^12 gas.
// For example, the block gas limit on NEAR is 1000 Tgas, and the transaction gas limit is 300 Tgas.
uint64 constant CALL_NEAR_GAS = 100_000_000_000_000;
uint64 constant CALLBACK_NEAR_GAS = 10_000_000_000_000;

/**
 * A simple oracle example deployed on an Aurora Silo which uses a Uniswap V2 pool (Trisolaris) as price feed from Aurora Mainnet.
 * The result of the XCC call is returned in the callback from Aurora Mainnet to the Silo.
 */
contract PythOracle is AccessControl {
    using AuroraSdk for NEAR;
    using AuroraSdk for PromiseCreateArgs;
    using AuroraSdk for PromiseWithCallback;
    using Codec for bytes;

    IPyth pyth;
    uint fee;
// Pyth Crypto.AURORA/USD	0x2f7c4f738d498585065a4b87b637069ec99474597da7f0ca349ba8ac3ba9cac5

    bytes32 public constant CALLBACK_ROLE = keccak256("CALLBACK_ROLE");

    string public auroraMainnetAccountId;
    NEAR public near;
    address public wNEAR;
    address public priceFeedAddr;

    uint256 public priceResult;
    uint8 targetPriceDecimals;

// For tetsting    
        bytes public theOutput;
        bytes public theData;
        int64 public priceCall;
        uint64 public confCall;
        int32 public expoCall;
        uint public publishTimeCall;
        int64 public testPriceCall;
        uint64 public testConfCall;
        int32 public testExpoCall;
        uint public testPublishTimeCall;
        bool public revertIfTrue;


    constructor(
        string memory _auroraMainnetAccountId,
        IERC20 _wNEAR,
        address _priceFeedAddr
    ) {
        auroraMainnetAccountId = _auroraMainnetAccountId;
        near = AuroraSdk.initNear(_wNEAR);

        wNEAR = address(_wNEAR);
        _grantRole(
            CALLBACK_ROLE,
            AuroraSdk.nearRepresentitiveImplicitAddress(address(this))
        );
        pyth = IPyth(_priceFeedAddr);
        priceFeedAddr = _priceFeedAddr;
    }

    function getPythPrice(bytes memory priceId, uint8 _targetPriceDecimals, bool _revertIfTrue) public { //returns (uint256) {
        targetPriceDecimals = _targetPriceDecimals;

        revertIfTrue = _revertIfTrue;

        bytes memory txData = abi.encodeWithSignature(
            "getPrice(bytes32)",
            priceId
        );
        PromiseCreateArgs memory callMainnetOracle = near.call(
            auroraMainnetAccountId,
            "call",
            abi.encodePacked(
                uint8(0),
                priceFeedAddr,
                uint256(0),
                txData.encode()
            ),
            0,
            CALL_NEAR_GAS
        );
        PromiseCreateArgs memory callback = near.auroraCall(
            address(this),
            abi.encodePacked(this.getPythPriceCallback.selector),
            0,
            CALLBACK_NEAR_GAS
        );

        callMainnetOracle.then(callback).transact();
        // return priceResult;
    }

    // This function is not meant to be called by an externally owned account (EOA) on Aurora.
    // It should only be invoked as a callback from the main `getPythPrice` method above. This is
    // the reason why this function has separate access control from `getPythPrice`.
    function getPythPriceCallback() public onlyRole(CALLBACK_ROLE) {
        if(revertIfTrue) revert("call back function is called");

        if (
            AuroraSdk.promiseResult(0).status != PromiseResultStatus.Successful
        ) {
            revert("getPythPrice call failed-NEAR");
        }
        bytes memory output = AuroraSdk.promiseResult(0).output;
        theOutput = output;
        if (output[1] != hex"00") {
            revert("getPythPrice call failed-Aurora");
        }
        bytes memory data = Borsh.decodeBytes(
            Borsh.from(BytesLib.slice(output, 2, output.length - 2))
        );
        theData = data;
        PythStructs.Price memory pyhtPrice = abi.decode(data, (PythStructs.Price));
        priceCall = pyhtPrice.price;
        confCall = pyhtPrice.conf;
        expoCall = pyhtPrice.expo;
        publishTimeCall = pyhtPrice.publishTime;
        priceCall = pyhtPrice.price;
        confCall = pyhtPrice.conf;
        expoCall = pyhtPrice.expo;
        publishTimeCall = pyhtPrice.publishTime;


        priceResult = convertToUint(pyhtPrice, targetPriceDecimals);
    }

    function testGetPythPrice(bytes memory priceId, uint8 _targetPriceDecimals) public returns (uint256) {
        targetPriceDecimals = _targetPriceDecimals;

        bytes memory txData = abi.encodeWithSignature(
            "getPriceUnsafe(bytes32)",
            priceId
        );
        PromiseCreateArgs memory callMainnetOracle = near.call(
            auroraMainnetAccountId,
            "call",
            abi.encodePacked(
                uint8(0),
                priceFeedAddr,
                uint256(0),
                txData.encode()
            ),
            0,
            CALL_NEAR_GAS
        );
        PromiseCreateArgs memory callback = near.auroraCall(
            address(this),
            abi.encodePacked(this.testGetPythPriceCallback.selector),
            0,
            CALLBACK_NEAR_GAS
        );

        callMainnetOracle.then(callback).transact();
        return priceResult;
    }

    // This function is not meant to be called by an externally owned account (EOA) on Aurora.
    // It should only be invoked as a callback from the main `getPythPrice` method above. This is
    // the reason why this function has separate access control from `getPythPrice`.
    function testGetPythPriceCallback() public onlyRole(CALLBACK_ROLE) {
        if (
            AuroraSdk.promiseResult(0).status != PromiseResultStatus.Successful
        ) {
            revert("getPythPrice call failed-NEAR");
        }
        bytes memory output = AuroraSdk.promiseResult(0).output;
        theOutput = output;
        if (output[1] != hex"00") {
            revert("getPythPrice call failed-Aurora");
        }
        bytes memory data = Borsh.decodeBytes(
            Borsh.from(BytesLib.slice(output, 2, output.length - 2))
        );
        theData = data;
        PythStructs.Price memory pyhtPrice = abi.decode(data, (PythStructs.Price));
        testPriceCall = pyhtPrice.price;
        testConfCall = pyhtPrice.conf;
        testExpoCall = pyhtPrice.expo;
        testPublishTimeCall = pyhtPrice.publishTime;

        priceResult = convertToUint(pyhtPrice, targetPriceDecimals);
    }



    function nearRepresentitiveImplicitAddress() public returns (address) {
        return AuroraSdk.nearRepresentitiveImplicitAddress(address(this));
    }

    function convertToUint(
        PythStructs.Price memory price,
        uint8 targetDecimals
    ) private pure returns (uint256) {
        if (price.price < 0 || price.expo > 0 || price.expo < -255) {
            revert("Invalid price");
        }

        uint8 priceDecimals = uint8(uint32(-1 * price.expo));

        if (targetDecimals >= priceDecimals) {
            return
                uint(uint64(price.price)) *
                10 ** uint32(targetDecimals - priceDecimals);
        } else {
            return
                uint(uint64(price.price)) /
                10 ** uint32(priceDecimals - targetDecimals);
        }
    }

}
